def getConfigValue(db, key, default=""):
  configdoc = db.getDocument(key)
  if configdoc:
    return configdoc.getItem('configValues')
  else:
    return default

def getConfigValuesAsList(db, key, default=[]):
  values = getConfigValue(db, key, default=None)
  if values is None:
    return default
  else:
    return values.replace('\r', '').split('\n')

def getConfigValuesAsDict(db, key, separator='|'):
  all = getConfigValuesAsList(db, key)
  d = {}
  for row in all:
    (label, key) = row.split(separator)
    d[key] = label
  return d

